catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '' || newValue == oldValue) {
        return;
    }

    //Type appropriate comment here, and begin script below

    var email = newValue + ''; // Email is always String.

    g_form.clearMessages();

    if (!email.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)) {
        g_form.addErrorMessage(email + " is not a valid email address. You must provide a valid email address. \[user@example.com\]");
        g_form.setValue('requester_email', '');
    }

    //Type appropriate comment here, and begin script below

}
